mapper.select:
select d.id as id,
d.name as name,
d.category_id as categoryId,
d.price as price,
d.image as image,
d.description as description,
d.status as status,
d.update_time as updateTime,
c.name as categoryName
from dish as d
left outer join category as c on d.category_id = c.id


        d.id = #{id}


        and d.name = #{name}


        and d.category_id = #{categoryId}


        and d.price = #{price}


        and d.image = #{image}


        and d.description = #{description}


        and d.status = #{status}

mapper.update:
delete from dish_flavor where dish_id = #{id};
insert into dish_flavor (dish_id, name, value) values
(#{flavor.dishId}, #{flavor.name}, #{flavor.value})

mapper.select:
select d.id as id, d.name as name, d.status as status, count(sd.id) as relatedCounts from dish as d left join setmeal_dish as sd on d.id = sd.dish_id where d.id in
#{id}
group by d.id

