mapper.select:
select id, dish_id, name, value
from dish_flavor
where id = #{flavorId}

select d.id as id, d.name as name, d.category_id as categoryId, d.price as price, d.image as image, d.description as description, d.status as status, d.update_time as updateTime, c.name as categoryName from dish as d left outer join category as c on d.category_id = c.id
d.name like concat('%', #{name}, '%' )
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.update_time desc
