mapper.select:
select s.id as id,
s.category_id as categoryId,
s.name as name,
s.price as price,
s.status as status,
s.description as description,
s.image as image,
s.update_time as updateTime,
c.name as categoryName
from setmeal as s
left join category as c on s.category_id = c.id


        s.id = #{id}


        and s.category_id = #{categoryId}


        and s.name = #{name}


        and s.price = #{price}


        and s.status = #{status}


        and s.description = #{description}


        and s.image = #{image}

mapper.update:
update setmeal
description = #{description},
status = #{status},
image = #{image},
category_id=#{categoryId}, name=#{name}, price=#{price}, update_time=#{updateTime}, update_user=#{updateUser}
where id = #{id}

update setmeal
set status=#{status}
where id = #{id}

mapper.delete:
delete
from setmeal
where id in

    #{id}
;
delete from setmeal_dish
where setmeal_id in

    #{id}

mapper.select:
select s.id as id, s.category_id as categoryId, s.name as name, s.price as price, s.status as status, s.description as description, s.image as image, s.update_time as updateTime, c.name as categoryName from setmeal as s left join category as c on s.category_id = c.id
s.name like concat('%', #{name}, '%' )
and s.category_id= #{categoryId}
and s.status= #{status}
order by s.update_time desc
