mapper.update:
update setmeal
description = #{description},
status = #{status},
image = #{image},
category_id=#{categoryId}, name=#{name}, price=#{price}, update_time=#{updateTime}, update_user=#{updateUser}
where id = #{id}

update setmeal
set status=#{status}
where id = #{id}

mapper.delete:
delete
from setmeal
where id in

    #{id}
;
delete from setmeal_dish
where setmeal_id in

    #{id}

mapper.select:
select * from setmeal


        name=
        #{setmealPageQueryDTO.name}


        and category_id=
        #{setmealPageQueryDTO.categoryId}


        and status=
        #{setmealPageQueryDTO.status}


order by update_time desc
