mapper.delete:
delete
from shopping_cart


        dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}

    or id = -1

mapper.update:
update shopping_cart set number = number - 1


        dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}

    or id = -1

mapper.select:
select * from shopping_cart


        and user_id = #{userId}


        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}


order by create_time desc

select * from shopping_cart


        name = #{name}


        and user_id = #{userId}


        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}


        and number = #{number}


        and amount = #{amount}


        and image = #{image}


        and create_time = #{createTime}
